movies = Movie.create([{title: "Avengers: Age of Ultron", description: "When Tony Stark tries to jumpstart a dormant peacekeeping program, things go awry and Earth’s Mightiest Heroes, including Iron Man, Captain America, Thor, The Incredible Hulk, Black Widow and Hawkeye, are put to the ultimate test as the fate of the planet hangs in the balance. As the villainous Ultron emerges, it is up to The Avengers to stop him from enacting his terrible plans, and soon uneasy alliances and unexpected action pave the way for an epic and unique global adventure.", genre_id: 1, year_released: 2015, image_link: "https://static.metacritic.com/images/products/movies/7/5c31ecea88c4736b0af2b127988f3b2e-250h.jpg"}, {title: "Airplane!", description: "A straight-faced yet absurdist comedy about a burned out lovelorn ex-pilot is pressed into service when an airliner's crew succumbs to food poisoning.", genre_id: 2, year_released: 1980, image_link: "https://static.metacritic.com/images/products/movies/9/69f4fe7abd034fc0c1a385ecaedd2137-250h.jpg"}, {title: "Terms of Endearment", description: "Following the mother-daughter relationship of Aurora Greenway (MacLaine) who doesn't like it when her daughter Emma (Winger) marries Flap (Daniels).", genre_id: 4, year_released: 1983, image_link: "https://static.metacritic.com/images/products/movies/1/1abfb7834a76e2c52188b5d8d7aef13f-98.jpg"}, {title: "Dumb & Dumber", description: "Two stupid guys, Lloyd (Carrey) and Harry (Daniels) decide to drive across country from Providence, Rhode Island to Aspen, Colorado to return a briefcase left in the airport by Mary Swanson (Holly), the object of Lloyd's affection.", genre_id: 2, year_released: 1994, image_link: "https://static.metacritic.com/images/products/movies/2/cbb1dc56593f3c3537d1b39735091077-250h.jpg"}, {title: "Arrival", description: "When mysterious spacecraft touch down across the globe, an elite team is put together to investigate – including language expert Louise Banks (Amy Adams). Mankind teeters on the verge of global war as everyone scrambles for answers – and to find them, Banks will take a chance that could threaten her life, and quite possibly humanity.", genre_id: 3, year_released: 2016, image_link: "https://static.metacritic.com/images/products/movies/8/e87cd20a7ef2e57a82a5c05b4fa0171e-250h.jpg"}, {title: "The Ring", description: "In this remake of one of Japan's biggest box office hits, Naomi Watts plays a journalist who discovers a mysterious videotape that is connected to several deaths.", genre_id: 5, year_released: 2002, image_link: "https://static.metacritic.com/images/products/movies/2/8b89b145b4ba3ee6a6cfcd4077dc8409-250h.jpg"}, {title: "Contact", description: "Jodie Foster stars as headstrong visionary astronomer Ellie Arroway in Contact, a drama of discovery, based on the best-selling 1985 novel by Pulitzer Prize-winning author and noted astronomer Carl Sagan.", genre_id: 3, year_released: 1997, image_link:"https://static.metacritic.com/images/products/movies/9/2484ac3cff696a7c0bfa1c9075f71774-250h.jpg"}, {title: "The Wizard of Oz", description: "Adapted from L. Frank Baum’s timeless children’s tale about a Kansas girl’s journey over the rainbow, The Wizard of Oz opened at Grauman’s Chinese Theater on August 15, 1939.", genre_id: 7, year_released: 1939, image_link: "https://static.metacritic.com/images/products/movies/0/d7b4eb297cce14e0278ff9870bf729a4-250h.jpg"}, {title: "Die Hard", description: "When terrorists take over a Los Angeles skyscraper on Christmas Eve, visting New York cop John McClane (Willis) thwarts their plans.", genre_id: 1, year_released: 1988, image_link: "https://static.metacritic.com/images/products/movies/2/b11ef7a183c8ca854c5cf42675e61a36-250h.jpg"}, {title: "Star Trek II: The Wrath of Khan", description: "With the aide of the Enterprise crew, Admiral Kirk must stop an old nemesis, Khan Noonien Singh, from using the Genesis Device as the ultimate weapon.", genre_id: 3, year_released: 1982, image_link: "https://static.metacritic.com/images/products/movies/7/7fc73d6b317830a2974eca459d24d711-98.jpg"}])
